body.script:
// Variables globales
let preguntas = [];
let respuestas = {};

// Cargar XML desde archivo externo
async function cargarXML() {
    try {
        const response = await fetch('preguntas.xml');
        if (!response.ok) {
            throw new Error(`Error al cargar XML: ${response.status}`);
        }
        const xmlString = await response.text();
        return parsearXML(xmlString);
    } catch (error) {
        console.error('Error:', error);
        document.getElementById('errorContainer').style.display = 'block';
        document.getElementById('errorContainer').textContent = 'Error al cargar el formulario. Verifica que el archivo preguntas.xml exista.';
        document.getElementById('loadingContainer').style.display = 'none';
        return [];
    }
}

// Parsear XML a objeto JavaScript
function parsearXML(xmlString) {
    try {
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlString, "text/xml");

        const parserError = xmlDoc.getElementsByTagName("parsererror");
        if (parserError.length > 0) {
            throw new Error("XML inv√°lido");
        }

        const preguntasNodes = xmlDoc.getElementsByTagName("pregunta");
        const nuevasPreguntas = [];

        for (let node of preguntasNodes) {
            const pregunta = {
                id: parseInt(node.getAttribute("id")),
                tipo: node.getAttribute("tipo"),
                requerida: node.getAttribute("requerida") === "true",
                texto: node.getElementsByTagName("texto")[0]?.textContent || "Pregunta sin texto"
            };

            // Descripci√≥n (opcional)
            const descripcion = node.getElementsByTagName("descripcion")[0];
            if (descripcion) {
                pregunta.descripcion = descripcion.textContent;
            }

            // Placeholder (para preguntas abiertas)
            const placeholder = node.getElementsByTagName("placeholder")[0];
            if (placeholder) {
                pregunta.placeholder = placeholder.textContent;
            }

            if (pregunta.tipo === "multiple") {
                pregunta.opciones = [];
                const opciones = node.getElementsByTagName("opcion");
                for (let opt of opciones) {
                    pregunta.opciones.push(opt.textContent);
                }
            }

            if (pregunta.tipo === "abierta") {
                pregunta.multilinea = node.getAttribute("multilinea") === "true";
            }

            if (pregunta.tipo === "deslizador") {
                pregunta.minimo = parseFloat(node.getAttribute("minimo")) || 0;
                pregunta.maximo = parseFloat(node.getAttribute("maximo")) || 100;
                pregunta.paso = parseFloat(node.getAttribute("paso")) || 1;
                pregunta.valor_defecto = parseFloat(node.getAttribute("valor_defecto")) || 50;
            }

            // Buscar condicionales
            const condicionales = node.getElementsByTagName("condicional");
            if (condicionales.length > 0) {
                pregunta.condicional = [];
                for (let cond of condicionales) {
                    pregunta.condicional.push({
                        depende: parseInt(cond.getAttribute("depende")),
                        mostrar_cuando: cond.getAttribute("mostrar_cuando")
                    });
                }
            }

            nuevasPreguntas.push(pregunta);
        }

        return nuevasPreguntas;
    } catch (error) {
        console.error("Error al parsear XML:", error);
        return [];
    }
}

// Renderizar formulario
function renderizarFormulario() {
    const form = document.getElementById("dynamicForm");
    form.innerHTML = "";

    preguntas.forEach(p => {
        const div = document.createElement("div");
        div.className = "form-group";
        div.id = `pregunta-${p.id}`;

        const titulo = document.createElement("div");
        titulo.className = "question-title";
        titulo.textContent = p.texto + (p.requerida ? " *" : "");
        div.appendChild(titulo);

        // Agregar descripci√≥n si existe
        if (p.descripcion) {
            const desc = document.createElement("small");
            desc.style.color = "#718096";
            desc.style.display = "block";
            desc.style.marginBottom = "10px";
            desc.textContent = p.descripcion;
            div.appendChild(desc);
        }

        switch(p.tipo) {
            case "multiple":
                const optionsDiv = document.createElement("div");
                optionsDiv.className = "options-group";

                p.opciones.forEach(opcion => {
                    const label = document.createElement("label");
                    label.className = "option-label";

                    const input = document.createElement("input");
                    input.type = p.opciones.length > 4 ? "checkbox" : "radio";
                    input.name = `p${p.id}`;
                    input.value = opcion;
                    input.dataset.preguntaId = p.id;

                    input.addEventListener("change", () => {
                        if (p.opciones.length > 4) {
                            const seleccionadas = Array.from(document.querySelectorAll(`input[name="p${p.id}"]:checked`))
                                .map(cb => cb.value);
                            respuestas[p.id] = seleccionadas;
                        } else {
                            respuestas[p.id] = input.value;
                        }
                        verificarCondicionales(p.id);
                    });

                    label.appendChild(input);
                    label.appendChild(document.createTextNode(opcion));
                    optionsDiv.appendChild(label);
                });

                div.appendChild(optionsDiv);
                break;

            case "abierta":
                if (p.multilinea) {
                    const textarea = document.createElement("textarea");
                    textarea.className = "open-input";
                    textarea.placeholder = p.placeholder || "Escribe tu respuesta...";
                    textarea.dataset.preguntaId = p.id;
                    textarea.addEventListener("input", (e) => {
                        respuestas[p.id] = e.target.value;
                    });
                    div.appendChild(textarea);
                } else {
                    const input = document.createElement("input");
                    input.type = "text";
                    input.className = "open-input";
                    input.placeholder = p.placeholder || "Escribe tu respuesta...";
                    input.dataset.preguntaId = p.id;
                    input.addEventListener("input", (e) => {
                        respuestas[p.id] = e.target.value;
                    });
                    div.appendChild(input);
                }
                break;

            case "deslizador":
                const sliderDiv = document.createElement("div");
                sliderDiv.className = "slider-container";

                const wrapper = document.createElement("div");
                wrapper.className = "slider-wrapper";

                const slider = document.createElement("input");
                slider.type = "range";
                slider.className = "slider";
                slider.min = p.minimo;
                slider.max = p.maximo;
                slider.step = p.paso;
                slider.value = p.valor_defecto;
                slider.dataset.preguntaId = p.id;

                const valor = document.createElement("span");
                valor.className = "slider-value";
                valor.textContent = p.valor_defecto;

                slider.addEventListener("input", (e) => {
                    valor.textContent = e.target.value;
                    respuestas[p.id] = parseFloat(e.target.value);
                });

                wrapper.appendChild(slider);
                wrapper.appendChild(valor);
                sliderDiv.appendChild(wrapper);
                div.appendChild(sliderDiv);

                respuestas[p.id] = p.valor_defecto;
                break;
        }

        form.appendChild(div);
    });

    // Aplicar condicionales
    preguntas.forEach(p => {
        if (p.condicional && p.condicional.length > 0) {
            const elemento = document.getElementById(`pregunta-${p.id}`);
            elemento.classList.add("conditional", "hidden");
        }
    });

    // Mostrar formulario
    document.getElementById('loadingContainer').style.display = 'none';
    form.style.display = 'block';
    document.getElementById('submitBtn').style.display = 'block';
}

// Verificar preguntas condicionales
function verificarCondicionales(preguntaId) {
    preguntas.forEach(p => {
        if (p.condicional) {
            const respuestaPadre = respuestas[preguntaId];
            const elemento = document.getElementById(`pregunta-${p.id}`);

            let debeMostrar = false;
            if (respuestaPadre) {
                if (Array.isArray(p.condicional)) {
                    debeMostrar = p.condicional.some(cond => 
                        cond.depende === preguntaId && 
                        (Array.isArray(respuestaPadre) ? 
                            respuestaPadre.includes(cond.mostrar_cuando) : 
                            respuestaPadre === cond.mostrar_cuando)
                    );
                } else if (p.condicional.depende === preguntaId) {
                    debeMostrar = Array.isArray(respuestaPadre) ? 
                        respuestaPadre.includes(p.condicional.mostrar_cuando) : 
                        respuestaPadre === p.condicional.mostrar_cuando;
                }
            }

            if (debeMostrar) {
                elemento.classList.remove("hidden");
            } else {
                elemento.classList.add("hidden");
                delete respuestas[p.id];
            }
        }
    });
}

// Validar formulario
function validarFormulario() {
    for (let p of preguntas) {
        if (p.requerida) {
            const respuesta = respuestas[p.id];
            const elemento = document.getElementById(`pregunta-${p.id}`);

            if (!elemento.classList.contains("hidden")) {
                if (respuesta === undefined || respuesta === null || respuesta === "" || 
                    (Array.isArray(respuesta) && respuesta.length === 0)) {
                    alert(`Por favor, responde: ${p.texto}`);
                    elemento.scrollIntoView({ behavior: "smooth" });
                    return false;
                }
            }
        }
    }
    return true;
}

// Exportar a Excel
function exportarExcel() {
    const wb = XLSX.utils.book_new();

    // Hoja de respuestas
    const respuestasData = Object.entries(respuestas).map(([id, respuesta]) => {
        const pregunta = preguntas.find(p => p.id === parseInt(id));
        return [
            id,
            pregunta ? pregunta.texto : "Desconocida",
            Array.isArray(respuesta) ? respuesta.join(", ") : respuesta.toString()
        ];
    });
    respuestasData.unshift(["ID", "Pregunta", "Respuesta"]);

    const wsRespuestas = XLSX.utils.aoa_to_sheet(respuestasData);
    XLSX.utils.book_append_sheet(wb, wsRespuestas, "Respuestas");

    // Hoja de resumen
    const resumenData = [
        ["FECHA", new Date().toLocaleString()],
        ["TOTAL PREGUNTAS", preguntas.length],
        ["RESPONDIDAS", Object.keys(respuestas).length],
        [""],
        ["DETALLE DE RESPUESTAS:"],
        ...Object.entries(respuestas).map(([id, respuesta]) => {
            const pregunta = preguntas.find(p => p.id === parseInt(id));
            return [pregunta ? pregunta.texto : "", Array.isArray(respuesta) ? respuesta.join(", ") : respuesta];
        })
    ];

    const wsResumen = XLSX.utils.aoa_to_sheet(resumenData);
    XLSX.utils.book_append_sheet(wb, wsResumen, "Resumen");

    // Guardar archivo
    const fecha = new Date().toISOString().split("T")[0];
    XLSX.writeFile(wb, `respuestas_${fecha}.xlsx`);
}

// Inicializaci√≥n
document.addEventListener("DOMContentLoaded", async () => {
    preguntas = await cargarXML();

    if (preguntas.length > 0) {
        renderizarFormulario();

        document.getElementById("submitBtn").addEventListener("click", () => {
            if (validarFormulario()) {
                document.getElementById("exportBtn").style.display = "block";
                alert("‚úÖ Formulario completado correctamente. Puedes descargar el Excel.");
            }
        });

        document.getElementById("exportBtn").addEventListener("click", exportarExcel);
    }
});
